mapper.insert:
INSERT INTO ACCOUNTING (
    TX_TRACE_NUM, INIT_TX_TRACE_NUM, FDM_PRODUCT_CODE, TX_BEHAV, TX_INT_ORG_NUM,
    ACC_ORG, TX_DT, TX_TRAN_ENTRY_DT, TX_TM, TX_CURR_CD, TX_TRADE_CODE, TX_AMT,
    TX_ACC_MODULE, STRK_BAL_TRACE_NUM, WRTOFF_IND, REV_DT
) VALUES (
    #{txTraceNum}, #{initTxTraceNum}, #{fdmProductCode}, #{txBehav}, #{txIntOrgNum},
    #{accOrg}, #{txDt}, #{txTranEntryDt}, #{txTm}, #{txCurrCd}, #{txTradeCode}, #{txAmt},
    #{txAccModule}, #{strkBalTraceNum}, #{wrtoffInd}, #{revDt}
)

mapper.update:
UPDATE ACCOUNTING
INIT_TX_TRACE_NUM = #{initTxTraceNum}, FDM_PRODUCT_CODE = #{fdmProductCode}, TX_BEHAV = #{txBehav}, TX_INT_ORG_NUM = #{txIntOrgNum}, ACC_ORG = #{accOrg}, TX_DT = #{txDt}, TX_TRAN_ENTRY_DT = #{txTranEntryDt}, TX_TM = #{txTm}, TX_CURR_CD = #{txCurrCd}, TX_TRADE_CODE = #{txTradeCode}, TX_AMT = #{txAmt}, TX_ACC_MODULE = #{txAccModule}, STRK_BAL_TRACE_NUM = #{strkBalTraceNum}, WRTOFF_IND = #{wrtoffInd}, REV_DT = #{revDt}
WHERE TX_TRACE_NUM = #{txTraceNum}
